mapper.select:
SELECT M.MSG_NO,M.MSGTITLE,M.MSGCONTENT,M.SEND_DATE,M.SENDER,M.RECIPIENT,M.READ_STATUS,M.STATUS,M.MSG_GROUP,E.EMP_NAME,D.DEPT_NAME,J.JOB_NAME FROM MESSENSER M
JOIN EMPLOYEE E ON (M.SENDER = E.EMPNO)
JOIN EMPLOYEE E ON (M.RECIPIENT = E.EMPNO)
JOIN DEPT_HISTORY DH ON(E.EMPNO = DH.EMPNO) JOIN DEPT D ON (D.DEPT_CODE = DH.DEPT_CODE) JOIN PROMOTION_HISTORY PH ON(PH.EMPNO = E.EMPNO) JOIN JOB J ON(J.JOB_CODE = PH.JOB_CODE) WHERE 1=1 AND M.MSG_NO = #{msgNo}
AND M.RECIPIENT = #{empNo}

mapper.update:
UPDATE MESSENSER
SET READ_STATUS = 'Y'
WHERE MSG_NO = #{msgNo}
